INSERT INTO `dbcb_options` VALUES("262", "_transient_dash_88ae138922fe95674369b1cb3d215a2b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/05/wordpress-4-5-2/\'>WordPress 4.5.2 Security Release</a> <span class=\"rss-date\">May 6, 2016</span><div class=\"rssSummary\">WordPress 4.5.2 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. WordPress versions 4.5.1 and earlier are affected by a SOME vulnerability through Plupload, the third-party library WordPress uses for uploading files. WordPress versions 4.2 through 4.5.1 are vulnerable to reflected XSS [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/woocommerce-releases-storefront-2-0-with-major-improvements-to-mobile-design\'>WPTavern: WooCommerce Releases Storefront 2.0 with Major Improvements to Mobile Design</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/timber-1-0-is-now-available-on-wordpress-org\'>WPTavern: Timber 1.0 Is Now Available on WordPress.org</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/wordpress-security-draft-podcast/\'>Post Status: WordPress Security — Draft podcast</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Popular Plugin:</span> UpdraftPlus Backup and Restoration&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=updraftplus&amp;_wpnonce=e739ca7fd4&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Install UpdraftPlus Backup and Restoration\">(Install)</a></li></ul></div>", "no");
