INSERT INTO `dbcb_postmeta` VALUES("795", "138", "titulo_1", "¿Por qué estudiar Administración y Negocios Internacionales?");
INSERT INTO `dbcb_postmeta` VALUES("796", "138", "_titulo_1", "field_5733588b375ea");
INSERT INTO `dbcb_postmeta` VALUES("797", "138", "parrafo_1", "Porque serás capaz de identificar las necesidades del consumidor global y las oportunidades del mercado mundial. Con ese conocimiento podrás participar en el desarrollo de productos y servicios de valor superior orientado a la exportación e importación.<br><br>\r\nConocerás todo lo referente al comercio internacional de bienes y servicios, expansión de mercados a nivel nacional e internacional o negociación intercultural. Estarás facultado paraenfrentardesafíos gerenciales de nivel global, resolver problemas en escenarios multidimensionales y multilingües y manejar con soltura las tecnologías de la información y las telecomunicaciones.");
