INSERT INTO `dbcb_postmeta` VALUES("819", "139", "titulo_1", "¿Por qué estudiar Arquitectura?");
INSERT INTO `dbcb_postmeta` VALUES("820", "139", "_titulo_1", "field_5733588b375ea");
INSERT INTO `dbcb_postmeta` VALUES("821", "139", "parrafo_1", "Porque como arquitecto habrás adquirido conocimientos de humanidades, ciencias físicas y sociales, tecnología, ciencias ambientales y artes creativas. Al ser una carrera científica, artística y humanística, se convierte en una de las más completas que hay. Su función es proponer soluciones creativas para el mejoramiento o transformación de la habitabilidad que requiere el ser humano para su desarrollo y confort.<br><br>\r\nTu formación como arquitecto combinará técnica y arte, historia y vanguardia, ciencia y letras, rigor y creatividad. Por esas razones, tu adaptabilidad al mundo laboral será una de las más versátiles que hay, y te permitirá incorporarte a casi cualquier tipo de tarea u oficio en diferentes entornos.\r\n\r\n");
INSERT INTO `dbcb_postmeta` VALUES("822", "139", "_parrafo_1", "field_573358c1375eb");
