INSERT INTO `dbcb_postmeta` VALUES("939", "145", "titulo_1", "¿Por qué estudiar Enfermería?");
INSERT INTO `dbcb_postmeta` VALUES("940", "145", "_titulo_1", "field_5733588b375ea");
INSERT INTO `dbcb_postmeta` VALUES("941", "145", "parrafo_1", "Como profesional de Enfermería tendrás un amplio campo laboral a nivel nacional e internacional. Se trata de la segunda carrera con mayor demanda laboral del Perú, y una de las más demandadas a nivel internacional. Como profesional de Enfermería no solo estarás en capacidad de trabajar en hospitales, clínicas y centros de salud diversos, podrás prestar servicios de asesoría a organizaciones nacionales e internacionales, gerenciar instituciones sanitarias y desarrollar investigación científica.");
