INSERT INTO `dbcb_postmeta` VALUES("1073", "152", "parrafo_1", "Como ingeniero de sistemas e informática podrás proponer soluciones innovadoras e integrales en diversos rubros productivos, educativos, del entretenimiento y las tecnologías de información y comunicación.<br><br>\r\n\r\nPorque habrás optado por una carrera que forma parte de las áreas priorizadas por el Estado peruano; y de una industria constituida por aproximadamente 300 empresas con una demanda creciente de ingenieros de sistemas e informática.");
