INSERT INTO `dbcb_postmeta` VALUES("1108", "154", "_titulo_1", "field_5733588b375ea");
INSERT INTO `dbcb_postmeta` VALUES("1109", "154", "parrafo_1", "Porque como profesional de la ingeniería eléctrica podrás desempeñarte en empresas de diversos rubros, que van desde el energético y extractivo hasta el de la industria, el comercio y la construcción.<br><br>\r\n\r\nPorque las condiciones geográficas del Perú lo convierten en un país idóneo para la puesta en marcha de diversos proyectos de generación y aprovechamiento de la energía eléctrica. Por esa razón la demanda laboral de egresados de la carrera irá en aumento en los próximos años, conforme se inicie la construcción de una veintena de hidroeléctricas en el territorio nacional que permitirá, además, la exportación de energía.");
INSERT INTO `dbcb_postmeta` VALUES("1110", "154", "_parrafo_1", "field_573358c1375eb");
