INSERT INTO `dbcb_postmeta` VALUES("1227", "159", "titulo_1", "¿Por qué estudiar Ingeniería Mecatrónica?");
INSERT INTO `dbcb_postmeta` VALUES("1228", "159", "_titulo_1", "field_5733588b375ea");
INSERT INTO `dbcb_postmeta` VALUES("1229", "159", "parrafo_1", "Porque como profesional de la Ingeniería Mecatrónica podrás integrar la mecánica con la electrónica de manera sinérgica para la mejora de procesos industriales o de manufactura a través de la creación de equipamientos automatizados controlados por computadora.<br><br>\r\n\r\nSerás capaz de diseñar, construir, instalar y operar a sistemas mecatrónicos en áreas de automatización, operación y mantenimiento.<br><br>\r\n\r\nAdemás estarás en capacidad de ejercer tu profesión como asesor, consultor, proyectista y contratista de obras y proyectos en tu propia empresa.<br><br>\r\n\r\nHay un importante campo de acción al que podrás acceder en empresas de rubros que van desde la minería hasta la industria, donde serás solicitado para la formulación y gestión de proyectos de automatización de procesos, maquinarias e integración de robots para la manufactura.<br><br>\r\n\r\nEn tu formación adquirirás capacidades para asumir responsabilidades de nivel operativo, táctico y estratégico.");
INSERT INTO `dbcb_postmeta` VALUES("1230", "159", "_parrafo_1", "field_573358c1375eb");
