INSERT INTO `dbcb_postmeta` VALUES("1325", "163", "parrafo_1", "Porque habrás optado por una carrera dedicada a la apertura de nuevos campos referidos a la investigación molecular y genética para la prevención e intervención de enfermedades degenerativas, además de la promoción de la salud y mejora de la calidad de vida.<br><br>\r\n\r\nSe trata de una carrera con alta demanda laboral debido a la rápida modernización del sector y al permanente uso de nuevo equipamiento y tecnología en la práctica médica.");
